            // ...$onboardingEmail, // temp disabled on 24/6/2023
        ];
        foreach (array_merge($emailDesigns) as $entry) {
            $entry['reference_key'] = $refKeyService->getRefKey(new EmailDesign());
            EmailDesign::updateOrCreate(['account_id' => $entry['account_id'], 'name' => $entry['name']], $entry);
        }
    }
}
